mapper.select:
select p.id, p.host_id, p.name, p.port, p.protocol, p.server_port, p.outer_port from cloud_host_open_port p
and p.host_id = #{hostId}
and p.protocol = #{protocol}
and p.port = #{port}
order by p.host_id, p.port, p.protocol

mapper.update:
update cloud_host_open_port
name = #{name}, server_port = #{serverPort}, outer_port = #{outerPort},
where id = #{id}
