mapper.select:
select count(1)
from sys_warnrule u

	1=1
	
		and u.ruletype = #{ruletype}
	
	
		and u.realtime = #{realtime}
	
	
		and u.notify_phone like #{notify_phone}
	
	
		and u.notify_email like #{notify_email}

select u.id, u.name, u.code, u.content, u.ruletype, u.isnotify, u.realtime, u.sendtime, u.notify_phone, u.notify_email, u.insert_user, date_format(u.insert_date,'%Y-%m-%d %H:%i:%s') as insert_date, a.name username, u.frequency, u.sampletime from sys_warnrule u left join operator a on u.insert_user = a.id
1=1
and u.ruletype = #{ruletype}
and u.realtime = #{realtime}
and u.notify_phone like #{notify_phone}
and u.notify_email like #{notify_email}
order by u.insert_date desc limit #{start_row}, #{row_count}
