mapper.select:
select ch.id id, ch.real_host_id, ch.warehouse_id, ch.user_id, ch.account, ch.password, ch.display_name, ch.sys_image_name, ch.host_name, ch.cpu_core, ch.memory, ch.sys_disk, ch.data_disk, ch.bandwidth, ch.running_status, ch.status, ch.inner_ip, ch.inner_port, ch.outer_ip, ch.outer_port, u.username userAccount, ch.type, m.name modelName, ch.assign_time from cloud_host ch left join sys_user u on ch.user_id = u.id left join cloud_host_warehouse chw on ch.warehouse_id = chw.id left join cloud_host_config_model m on chw.cloud_host_config_model_id = m.id join host_timer_relationship ship on ch.id = ship.host_id where ch.user_id is not null and running_status = 1 and ship.timer_key = #{timerKey}
and (ship.last_back_up_time is null or ship.last_back_up_time < #{now})
limit #{limit}

select ch.id id, ch.real_host_id, ch.warehouse_id, ch.user_id, ch.account, ch.password, ch.display_name, ch.sys_image_name, ch.host_name, ch.cpu_core, ch.memory, ch.sys_disk, ch.data_disk, ch.bandwidth, ch.running_status, ch.status, ch.inner_ip, ch.inner_port, ch.outer_ip, ch.outer_port, ch.type, ch.assign_time from cloud_host ch join host_timer_relationship ship on ch.id = ship.host_id where ch.running_status = 1 and ship.timer_key = #{timerKey}
and (ship.last_back_up_time is null or ship.last_back_up_time < #{now})
limit #{limit}

mapper.update:
update cloud_host

	user_id = #{userId} ,
	assign_time = #{assignTime}

where id = #{id}

update cloud_host

	  
		inner_port = #{innerPort}, 
		outer_ip = #{outerIp} 

where real_host_id = #{realHostId}

update cloud_host

	sys_image_id = #{sysIamgeId},
	sys_image_name = #{sysImageName}

where id = #{id}

update cloud_host 
	
		cpu_core = #{cpuCore},
		memory = #{memory} ,
		bandwidth = #{bandwidth}
	
where id = #{id}

mapper.select:
SELECT ch.id id, ch.real_host_id, ch.warehouse_id, ch.user_id, ch.account, ch.password, ch.display_name, ch.sys_image_name, ch.host_name, ch.cpu_core, ch.memory, ch.sys_disk, ch.data_disk, ch.bandwidth, ch.running_status, ch.status, ch.inner_ip, ch.inner_port, ch.outer_ip, ch.outer_port, u.username userAccount, ch.type, m.name modelName, ch.assign_time FROM cloud_host ch LEFT JOIN sys_user u ON ch.user_id = u.id LEFT JOIN cloud_host_warehouse chw ON ch.warehouse_id = chw.id LEFT JOIN cloud_host_config_model m ON chw.cloud_host_config_model_id = m.id JOIN host_timer_relationship ship ON ch.id = ship.host_id WHERE ship.timer_key = #{key}
AND ch.user_id IS NOT NULL AND ch.type = 1
AND ch.type = 2
