		name = #{name},
		cloud_host_config_model_id = #{cloudHostConfigModelId},
		total_amount = #{totalAmount},
		modify_time = #{modifyTime}
	
where id = #{id}

update cloud_host_warehouse 
	
		remain_amount = remain_amount + 1 
	
where id = #{id}

