SELECT billid, usercount, password, email, telphone, status, date_format(insert_date,'%Y-%m-%d %H:%i:%s') as insert_date, insert_user, displayname, user_type FROM man_system_user
status != 9
AND (usercount LIKE #{param} OR email LIKE #{param} OR displayname LIKE #{param})
AND status = #{status}
AND user_type = #{userType}
ORDER BY insert_date DESC

select 	billid,
		usercount,
		password,
		email,
		telphone,
		status,
		usb_status,
		date_format(insert_date,'%Y-%m-%d %H:%i:%s') as insert_date,
		insert_user,
		displayname, 
		user_type
from man_system_user
where 1=1 

		and usercount = #{usercount}


		and password = #{password}


		and email = #{email}


		and telphone = #{telphone} 


		and status = #{status}  


		and displayname = #{displayname}  

	and status != 9
