mapper.select:
select o.id, o.user_id, s.usercount username, o.oper_time, o.user_ip, o.module, o.content, o.status, o.level from oper_log o left join man_system_user s on o.user_id = s.billid
o.status != 5
and s.usercount like #{username}
and o.status = #{status}
and o.level = #{level}
order by o.oper_time desc

select o.id, o.user_id, s.usercount username, o.oper_time, o.user_ip, o.module, o.content, o.status, o.level from oper_log o left join man_system_user s on o.user_id = s.billid
o.status != 5
AND (s.usercount LIKE #{param} OR o.module LIKE #{param} OR o.content LIKE #{param})
and o.status = #{status}
and o.level = #{level}
order by o.oper_time desc limit #{start_row}, #{row_count}

select count(o.id) from oper_log o left join man_system_user s on o.user_id = s.billid
o.status != 5
and (s.usercount like #{param} OR o.module LIKE #{param} OR o.content LIKE #{param} )
and o.status = #{status}
and o.level = #{level}
