mapper.select:
SELECT t.id, t.real_image_id, t.name, t.from_host_id, t.type, t.description, t.display_name, t.status, t.create_time, t.image_type, t.file_type FROM sys_disk_image t
1=1
AND t.name LIKE #{name}
AND t.type = #{type}
AND t.status = #{status}
AND t.image_type = #{image_type}
order by t.name

mapper.update:
		update sys_disk_image set 
			name          = #{name},  
  			description   = #{description},
			display_name  = #{displayName}, 
			type          = #{type}
			
			,modified_time = #{modifiedTime}
			
 		where id = #{id}

update sys_disk_image 

	
		real_image_id = #{realImageId}
	 
	
		,file_type = #{fileType}
	 
	 

where  name = #{name}
and real_image_id is null

update sys_disk_image set
image_type = #{imageType}
where  id  = #{id}

mapper.delete:
delete from sys_disk_image
where id in 

