mapper.select:
select * from sys_user u
join terminal_user tu on u.id = tu.id
where u.username = #{username}
and u.password = #{password}

and type = #{type}

and tu.status != 9
limit 1

select * from sys_user u
join terminal_user tu on u.id = tu.id
where u.group_id = #{group_id}

and type = #{type}

and tu.status != 9
order by u.username asc

select * from sys_user u join terminal_user tu on u.id = tu.id where u.group_id not in (select id from sys_group)
and type = #{type}
and tu.status != 9

mapper.update:
update sys_user 		
set password = #{password},
modified_time = #{modified_time}
where  id = #{id}

update sys_user set 
	group_id = #{group_id},
	alias = #{alias}
where id = #{id}

update sys_user set
group_id = #{group_id}
where id = #{id}

update sys_user set
group_id = '0'
where id = #{id}

mapper.delete:
delete u.*
from sys_user u
where id = #{id}

mapper.update:
update sys_user 		
set alias = #{alias},
modified_time = #{modified_time}
where  id = #{id}
