mapper.select:
select a.id, a.group_id, b.group_name, a.title, a.content, a.create_time, a.region, a.industry from terminal_information_push a left join sys_group b on a.group_id = b.id where 1=1
and a.create_time > #{startTime}
and a.create_time < #{endTime}
order by a.create_time desc

select a.id, a.group_id, b.group_name, a.title, a.content, a.create_time, a.region, a.industry from terminal_information_push a left join sys_group b on a.group_id = b.id where
a.create_time > #{time}
and (a.group_id = 'all' or a.group_id = #{groupId})
and a.region = #{region}
and a.industry = #{industry}
order by a.create_time desc
